import React from "react";
import "./artist.scss";

class Artist extends React.Component {
  constructor(props) {
    super(props);
  }
  state = {
    active: false,
  };
  togglePlayHandler = () => {
    const currentState = this.state.active;
    this.setState({
      active: currentState,
    });
  };
  render() {
    return (
      <div id="artist">
        {this.props.data.map((item, idx) => (
          <div key={idx} className="{this.state.active ? 'classNmae'">
            <figure>
              <a href="">
                <img
                  src={require(`../../assets/images/${item.image}`)}
                  alt={item.singerName}
                />
              </a>
            </figure>
            <figcaption>{item.singerName}</figcaption>
            <p>{item.albumsName}</p>
            {this.state.isPlaying ? (
              <i className="las la-pause"></i>
            ) : (
              <i className="las la-play" onClick={this.togglePlayHandler}></i>
            )}
            ;
          </div>
        ))}
      </div>
    );
  }
}

export default Artist;
