import React from "react";
import axios from 'axios';
import Aside from "./components/aside/aside";
import Content from "./components/content/content";
import Header from "./components/header/header";
import Footer from "./components/footer/footer";

import "./App.scss";

class App extends React.Component {
  constructor(props) {
    super(props);
  }

  state = {
    currentPage: "home",
    searchText: "",
    isLoaded: false,
    track: {
      image: "arash.jpeg",
      trackName: "harchi_1",
      trackUrl: "harchi_1.mp3",
      singerName: "Arash"
    },
    data: {
      recently: {
        isPlaying: false,
        title: "Recently played",
        tracks: [
          {
            image: "Katy_Perry_Smile.png",
            singerName: "Kety Perry",
            albumsName: "smile",
          },
          {
            image: "arash.jpeg",
            singerName: "Arash",
            albumsName: "harchi",
          },
          {
            image: "alesso.jpeg",
            singerName: "alesso",
            albumsName: "chert",
          }
        ]
      },
      all: {
        title: "Tracks list",
        tracks: [
          {
            image: "Katy_Perry_Smile.png",
            singerName: "Kety Perry",
            albumsName: "smile",
          },
          {
            image: "arash.jpeg",
            singerName: "Arash",
            albumsName: "harchi",
          },
          {
            image: "alesso.jpeg",
            singerName: "alesso",
            albumsName: "chert",
          },
          {
            image: "rap.jpg",
            singerName: "Adel",
            albumsName: "adel",
          },
          {
            image: "arash.jpeg",
            singerName: "Arash",
            albumsName: "harchi",
          },
          {
            image: "alesso.jpeg",
            singerName: "alesso",
            albumsName: "chert",
          }
        ]
      }
    }
  };

  handleAsideClick = (page) => {
    this.setState({
      currentPage: page,
    });
    console.log("page:", page);
  };

  handleSearch = (singer) => {
    console.clear();
    console.log(singer);
    this.loadAlbums(singer);
  };

  loadAlbums = (singer) => {
    const access_token = "";
    const headers = {
      'Authorization': `token ${access_token}`
    }
    axios.get(
      "https://api.spotify.com/v1/search",
      {
        headers,
        params,
      }
    )
    fetch("https://api.spotify.com/v1/search")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            tracks: result.items
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )

    // const recivedData = [
    //   {
    //     singer: "fari 1",
    //     track: "track 1",
    //   },
    //   {
    //     singer: "fari 2",
    //     track: "track 2",
    //   },
    // ];

    // this.setState({
    //   data: recivedData,
    // });
  };

  loadMore = () => {
    let newTracks = this.state.tracks;
    let newData = newTracks.all.data;
    newData = [...newData, ...newData];
    newTracks.all.data = newData;

    this.setState({
      tracks: newTracks,
    });

    console.log("loadMore: ", newTracks)
  }

  playMusic = (title, idx) => {
    const data = this.state.data;
    let newData = { ...data };
    let newRecentlyTracks = newData.recently.tracks;

    if (title == 'Recently played') {
      const removedTrack = newRecentlyTracks.splice(idx, 1);
      newRecentlyTracks = [removedTrack[0], ...newRecentlyTracks];
    } else if (title == "Tracks list") {
      const selectedTrackFromAll = this.state.data.all.tracks[idx];
      newRecentlyTracks = [selectedTrackFromAll, ...newRecentlyTracks];
    }

    let isPlaying = true;
    if (idx === 0 && title === "Recently played") {
      isPlaying = !this.state.data.recently.isPlaying;
    }

    newRecentlyTracks = newRecentlyTracks.slice(0, 6);
    newData.recently.tracks = newRecentlyTracks;
    newData.recently.isPlaying = isPlaying;

    this.setState({
      data: newData
    })
  }

  render() {
    return (
      <>
        <Aside action={this.handleAsideClick} />
        <Header
          page={this.state.currentPage}
          title={this.state.currentPage}
          action={this.handleSearch}
        />
        <Content
          data_app={this.state.data}
          scroll_app={this.loadMore}
          play_app={this.playMusic}
        />
        <Footer data={this.state.track} />
      </>
    );
  }
}

export default App;
