import React from "react";
import Aside from "./components/aside/aside";
import Content from "./components/content/content";
import Header from "./components/header/header";
import Footer from "./components/footer/footer";

import "./App.scss";

class App extends React.Component {
  constructor(props) {
    super(props);
  }

  state = {
    currentPage: "home",
    searchText: "",
    data: [
      {
        singer: "hadi",
        track: "first 1",
      },
      {
        singer: "hado",
        track: "first 2",
      },
    ],
  };

  handleAsideClick = (page) => {
    this.setState({
      currentPage: page,
    });
    console.log("page:", page);
  };

  handleSearch = (singer) => {
    console.clear();
    console.log(singer);
    this.loadAlbums(singer);
  };

  loadAlbums = (singer) => {
    // API address
    // Token
    // Singer

    // fetch("API address", {PARAMS});
    // axios.get()

    const recivedData = [
      {
        singer: "fari 1",
        track: "track 1",
      },
      {
        singer: "fari 2",
        track: "track 2",
      },
    ];

    this.setState({
      data: recivedData,
    });
  };

  render() {
    return (
      <div className="homePage">
        <Aside action={this.handleAsideClick} />

        <div className="wrapper">
          <Header
            page={this.state.currentPage}
            title={this.state.currentPage}
            action={this.handleSearch}
          />
          <Content data={this.state.data} />
        </div>
        <Footer />
      </div>
    );
  }
}

export default App;
