import React from "react";
import Logo from "../logo/logo";
import Btn from "../btn/btn";

import "./aside.scss";

class Aside extends React.Component {
  state = {
    pages: [
      {
        title: "Home",
        page: "test page 1",
        icon: "home",
      },
      {
        title: "Search",
        page: "test page 2",
        icon: "search",
      },
      {
        title: "Your Library",
        page: "test page 2",
        icon: "list",
      },
      {
        title: "Create Playlist",
        page: "test page 2",
        icon: "folder-plus",
      },
      {
        title: "Liked Songs",
        page: "test page 2",
        icon: "heart",
      },
    ],
  };

  handleClick = (page) => {
    this.props.action(page);
  };

  render() {
    return (
      <aside className="aside">
        <Logo />
        {this.state.pages.map((item, idx) => (
          <Btn
            key={idx}
            title={item.title}
            page={item.page}
            icon={item.icon}
            action={this.handleClick}
          />
        ))}
      </aside>
    );
  }
}

export default Aside;
