import React from "react";
import "./header.scss";

class Header extends React.Component {
  constructor(props) {
    super(props);
  }

  state = {
    searchText: "",
  };

  handleSearchText(e) {
    console.clear();
    const text = e.targt.value;
    console.log(text);
    this.setState({
      searchText: text,
    });
  }

  render() {
    return (
      <>
        <input
          type="text"
          value={this.state.searchText}
          onChange={handleSearchText}
        />
        <div className="header">{this.props.title}</div>
      </>
    );
  }
}

export default Header;
