import React from "react";
import Btn from "../btn/btn";

import "./album.scss";

class Album extends React.Component {
  constructor(props) {
    super(props);
  }
  state = {
    tracks: [
      {
        image: "Katy_Perry_Smile.png",
        singerName: "Kety Perry",
        albumsName: "smile",
      },
      {
        image: "arash.jpeg",
        singerName: "Arash",
        albumsName: "harchi",
      },
      {
        image: "alesso.jpeg",
        singerName: "alesso",
        albumsName: "chert",
      },
    ],
  };

  render() {
    return (
      <div id="album">
        <header>
          <figure>
            <im
              src={require(`../../assets/images/${this.state.tracks.image}`)}
            />
          </figure>
          <div id="discription">
            <h1>This Is Katy Perry</h1>
            <p>The essential tracks, all in one playlist.</p>
          </div>
        </header>

        <section>
          <header>
            <Btn />
          </header>
        </section>
      </div>
    );
  }
}

export default Album;
