import React from "react";
import axios from 'axios';
import Aside from "./components/aside/aside";
import Content from "./components/content/content";
import Header from "./components/header/header";
import Footer from "./components/footer/footer";

import "./App.scss";

class App extends React.Component {
  constructor(props) {
    super(props);
  }

  access_token = "BQDXyRH5l-MCJLcfjqQ3gs6JNGP_7olaziQsUo4ooPjFjeTKIeUoDnX2PN2754cDlzzN1MyDSxRKjcNnrWtHQnhDXurfjoUOkgLWOFUxjOkPIr4ciZpMfoK6Crc81TkwJFIAjEsHReBN_p5nlaebWNSxzuMtikym2Lo";
  headers = {
    'Authorization': `Bearer ${this.access_token}`,
    'Content-Type': 'application/json',
    'Accept': 'application/json',
  };

  state = {
    currentPage: "home",
    searchText: "",
    isLoaded: false,
    offset: 0,
    play: {
      section: '',
      idx: null,
      active: false,
    },
    track: {
      image: "arash.jpeg",
      trackName: "harchi_1",
      trackUrl: "harchi_1.mp3",
      singerName: "Arash"
    },
    data: {
      recently: {
        id: 1,
        title: "Recently played",
        tracks: [
          {
            image: "Katy_Perry_Smile.png",
            singerName: "Kety Perry",
            albumsName: "smile",
          },
          {
            image: "arash.jpeg",
            singerName: "Arash",
            albumsName: "harchi",
          },
          {
            image: "alesso.jpeg",
            singerName: "alesso",
            albumsName: "chert",
          },

        ]
      },
      all: {
        id: 2,
        title: "Tracks list",
        tracks: [
          {
            image: "Katy_Perry_Smile.png",
            singerName: "Kety Perry",
            albumsName: "smile",
          },
          {
            image: "arash.jpeg",
            singerName: "Arash",
            albumsName: "harchi",
          },
          {
            image: "alesso.jpeg",
            singerName: "alesso",
            albumsName: "chert",
          },
          {
            image: "rap.jpg",
            singerName: "Adel",
            albumsName: "adel",
          },
          {
            image: "arash.jpeg",
            singerName: "Arash",
            albumsName: "harchi",
          },
          {
            image: "alesso.jpeg",
            singerName: "alesso",
            albumsName: "chert",
          }
        ]
      }
    },
    artists: {
      "6jJ0s89eD6GaHleKKya26X": [ // artist id
        {
          album_id: "fsjydf78sd6fygjdhf", // album id
          is_loaded: false,
        },
        {
          id: "sdfsdfsdfsdfsdfsdf", // album id
          is_loaded: false,
        }
      ],
      "6EdXBTjIDwu5aYv4U3K8TI": [1, 2, 3],
      "5EUdiv20t58GCS09VMKk7M": [1],
      "106MIqAGadRmicPsEfwZTn": [1, 2, 3, 4, 5],
    }
  };

  handleAsideClick = (page) => {
    this.setState({
      currentPage: page,
    });
  };

  loadTracks = (albumId) => {
    const access_token = "BQAeNOoxz2legi_hzUJVYC8TCkTkfcHawhPB_mPDD_enJjYhg3yp4nvcZzqm39RfOsnYnk7ktUi6LwGlOBgd6sc7Om9OsGLhZ8ONh4DbeiRuJJkqj3fSB4DexW6O6x2Ix9iX_FMFPbARjApvNvXOlsUJY1btWXa2OjE";
    const headers = {
      'Authorization': `Bearer ${access_token}`,
      'Content-Type': 'application/json',
      'Accept': 'application/json',
    };

    axios.get(
      `https://api.spotify.com/v1/albums/${albumId}/tracks`,
      { headers: headers }
    ).then(res => {
      console.log(res)
      // if (res.data) {
      //   const tracks = res.data.tracks.items;
      //   console.log(tracks)
      // }
    }).catch(err => {
      console.log(err)
    })
  }

  loadArtist = (artistName) => {
    const params = `q=${artistName}&type=artist&offset=${this.state.offset}&limit=6`;

    axios.get(
      `https://api.spotify.com/v1/search?${params}`,
      { headers: this.headers }
    ).then(res => {
      if (res.data) {
        const artistsIdList = res.data.artists.items.map(item => item.id);
        const artists = {};
        artistsIdList.map(artistId => {
          artists[artistId] = this.loadAlbums(artistId);
        });
        console.log(artists);
        this.setState({
          artists: artists
        })
      }
    }).catch(err => {
      console.log(err)
    })
  };

  loadAlbums = (artistId) => {
    axios.get(
      `https://api.spotify.com/v1/artists/${artistId}/albums`,
      { headers: this.headers }
    ).then(res => {
      if (res.data) {
        const albums = res.data.items;
        const albumsList = albums.map(item => {
          return {
            album_id: item.id, // album id
            is_loaded: false,
          }
        });
        return albumsList;
      }
    }).catch(err => {
      console.log(err)
    })
  }

  loadMore = () => {
    this.loadArtist();
    return

    let newTracks = [...this.state.data];
    let newData = [newTracks.all.tracks];
    newData = [...newData, ...newData];
    newTracks.all.tracks = newData;

    this.setState({
      data: newTracks,
    });

    console.log("loadMore: ", newTracks)
  }

  playMusic = (title, idx) => {
    let newData = { ...this.state.data };
    let newRecentlyTracks = [...newData.recently.tracks];

    if (title == 'Recently played') {
      const removedTrack = newRecentlyTracks.splice(idx, 1);
      newRecentlyTracks = [removedTrack[0], ...newRecentlyTracks];
    } else if (title == "Tracks list") {
      const selectedTrackFromAll = this.state.data.all.tracks[idx];
      newRecentlyTracks = [selectedTrackFromAll, ...newRecentlyTracks];
    }

    let newPlay = { ...this.state.play };

    let isPlaying = true;
    if (idx === 0 && title === "Recently played") {
      isPlaying = !this.state.data.recently.isPlaying;
    }

    newPlay.section = title;
    newPlay.idx = idx;
    newPlay.active = isPlaying;

    newRecentlyTracks = newRecentlyTracks.slice(0, 6);
    newData.recently.tracks = newRecentlyTracks;
    newData.recently.isPlaying = isPlaying;

    this.setState({
      play: newPlay,
    }, () => {
      console.log(this.state.play)
    });


    this.setState({
      data: newData,
    }, () => {
      console.log(this.state.play)
    });

  }

  render() {
    return (
      <>
        <Aside action={this.handleAsideClick} />
        <Header
          page={this.state.currentPage}
          title={this.state.currentPage}
          action={this.loadArtist}
        />
        <Content
          data_app={this.state.data}
          scroll_app={this.loadMore}
          playMusic_app={this.playMusic}
          play_app={this.state.play}
        />
        <Footer data={this.state.track} />
      </>
    );
  }
}

export default App;
