import React from "react";
import Aside from "./components/aside/aside";
import Content from "./components/content/content";
import Header from "./components/header/header";
import Footer from "./components/footer/footer";

import "./App.scss";

class App extends React.Component {
  constructor(props) {
    super(props);
  }

  state = {
    currentPage: "home",
    searchText: "",
    track: {
      image: "arash.jpeg",
      trackName: "harchi_1",
      trackUrl: "harchi_1.mp3",
      singerName: "Arash"
    },
    data: {
      recently: [],
      albums: [
        {
          image: "Katy_Perry_Smile.png",
          singerName: "Kety Perry",
          albumsName: "smile",
        },
        {
          image: "arash.jpeg",
          singerName: "Arash",
          albumsName: "harchi",
        },
        {
          image: "alesso.jpeg",
          singerName: "alesso",
          albumsName: "chert",
        },
        {
          image: "rap.jpg",
          singerName: "Adel",
          albumsName: "adel",
        }
      ]
    }
  };

  handleAsideClick = (page) => {
    this.setState({
      currentPage: page,
    });
    console.log("page:", page);
  };

  handleSearch = (singer) => {
    console.clear();
    console.log(singer);
    this.loadAlbums(singer);
  };

  loadAlbums = (singer) => {
    // API address
    // Token
    // Singer

    // fetch("API address", {PARAMS});
    // axios.get()

    const recivedData = [
      {
        singer: "fari 1",
        track: "track 1",
      },
      {
        singer: "fari 2",
        track: "track 2",
      },
    ];

    this.setState({
      data: recivedData,
    });
  };

  loadMore = () => {
    let newState = this.state.data;
    newState = [...newState, ...newState];
    this.setState({
      data: newState,
    });
    console.log("APP");
  }
  render() {
    return (
      <>
        <Aside action={this.handleAsideClick} />
        <Header
          page={this.state.currentPage}
          title={this.state.currentPage}
          action={this.handleSearch}
        />
        <Content
          recentlyPlayed_app={this.state.data.recently}
          tracks_app={this.state.data.albums}
          action={this.loadMore}
        />
        <Footer data={this.state.track} />
      </>
    );
  }
}

export default App;
