import React from "react";
import Btn from "../btn/btn";

import "./album.scss";

class Album extends React.Component {
  constructor(props) {
    super(props);
  }
  state = {};
  render() {
    return (
      <div id="album">
        <header>
          <figure>
            <img
              src={require(`../../assets/images/${item.image}`)}
              alt={item.singerName}
            />
          </figure>
          <div id="discription">
            <h1>This Is Katy Perry</h1>
            <p>The essential tracks, all in one playlist.</p>
          </div>
        </header>

        <section>
          <header>
            <Btn />
          </header>
        </section>
      </div>
    );
  }
}

export default Album;
