import React from "react";
import "./artist.scss";

class Artist extends React.Component {
  constructor(props) {
    super(props);
  }

  state = {
    artists: [],
    isPlaying: false,
  };

  componentDidMount() {
    const artists = this.props.data;
    this.setState({
      artists,
    });
  }

  playHandler = (idx) => {
    console.clear();
    console.log(idx);
    const artists = [...this.state.artists];
    artists.splice(idx, 1);
    const newArtists = [this.state.artists[idx], ...artists];

    this.setState({
      artists: newArtists,
    });

    // const currentState = this.state.isPlaying;
    // this.setState({
    //   isPlaying: !currentState,
    // });
  };

  render() {
    return (
      <div id="artist">
        {this.state.artists.map((item, idx) => (
          <div
            onClick={() => {
              this.playHandler(idx);
            }}
            key={idx}
            className={
              this.state.isPlaying === false ? "albums" : "albums active"
            }
          >
            <figure>
              <img
                src={require(`../../assets/images/${item.image}`)}
                alt={item.singerName}
              />
            </figure>
            <figcaption>{item.singerName}</figcaption>
            <p>{item.albumsName}</p>
            {this.state.isPlaying ? (
              <i className="las la-pause" onClick={this.playHandler}></i>
            ) : (
              <i className="las la-play"></i>
            )}
          </div>
        ))}
      </div>
    );
  }
}

export default Artist;
