import React from "react";
import { BrowserRouter, Routes, Route } from "react-router-dom";
import AlbumsList from "../albumsList/albumsList";
import Album from "../album/album";

import "./content.scss";

class Content extends React.Component {
  handleScroll = (e) => {
    const el = e.target;
    const bottom = el.scrollHeight - el.scrollTop === el.clientHeight;
    if (bottom) {
      this.props.scroll_app();
    }
  };

  render() {
    return (
      <BrowserRouter>
        <div id="content" onScroll={this.handleScroll}>
          <Routes>
            <Route
              exact
              path="/"
              element={
                <AlbumsList
                  data_content={this.props.data_app.recently}
                  playMusic_content={this.props.playMusic_app}
                  play_content={this.props.play_app}
                ></AlbumsList>
              }
            ></Route>
            <Route exact path="/play" element={<Album />}></Route>
          </Routes>
        </div>
      </BrowserRouter>
    );
  }
}

export default Content;
