import React from "react";
import Artist from "../artist/artist";

import "./content.scss";

class Content extends React.Component {
  state = {
    data: [
      {
        image: "Katy_Perry_Smile.png",
        singerName: "Kety Perry",
        albumsName: "smile",
      },
      {
        image: "arash.jpeg",
        singerName: "Arash",
        albumsName: "harchi",
      },
      {
        image: "alesso.jpeg",
        singerName: "alesso",
        albumsName: "chert",
      },
      {
        image: "rap.jpg",
        singerName: "nemidonam",
        albumsName: "haminjoori",
      },
    ],
  };
  render() {
    return (
      <div className="content">
        <Artist data={this.state.data} />
        <Footer data={this.state.data} />
      </div>
    );
  }
}

export default Content;
