import React from "react";
import "./track.scss";

class Track extends React.Component {
  state = {
    playing: 1,
  };

  isPlaying = () => {
    return this.props.isPlaying;
  };

  render() {
    return (
      <div className={"track" + (this.isPlaying() ? " playing" : "")}>
        <span>
          {this.isPlaying() && (
            <>
              <span>
                <i className="las la-music"></i>
              </span>
              <span>
                <i className="las la-pause"></i>
              </span>
            </>
          )}
          {!this.isPlaying() && (
            <>
              <span>{this.props.id + 1}</span>
              <span>
                <i className="las la-play"></i>
              </span>
            </>
          )}
        </span>
        <span>
          <figure>
            <img
              src={require(`../../assets/images/${this.props.info.image}`)}
              alt=" {this.props.info.title}"
            />
          </figure>
          <span>
            <figcaption>{this.props.info.title}</figcaption>
            <figcaption>{this.props.info.singer}</figcaption>
          </span>
        </span>
        <span>{this.props.info.album}</span>
        <span>{this.props.info.date}</span>
        <span>{this.props.info.time}</span>
      </div>
    );
  }
}

export default Track;
