import React from "react";
import "./trackList.scss";

class TrackList extends React.Component {
  constructor(props) {
    super(props);
  }

  playHandler = (idx) => {
    let newStatus = !this.state.isPlaying;

    if (idx !== 0) {
      newStatus = true;
      const artists = [...this.props.tracks];
      artists.splice(idx, 1);
      const newArtists = [this.props.tracks[idx], ...artists];

      this.setState({
        artists: newArtists,
      });
    }

    this.setState({
      isPlaying: newStatus,
    });
  };

  render() {
    return (
      <div id="trackList">
        <h2>{this.props.title}</h2>
        {this.props.tracks.map((item, idx) => (
          <div
            onClick={() => {
              this.playHandler(idx);
            }}
            key={idx}
            className={
              "albums" + (this.state.isPlaying && idx === 0 ? " active" : "")
            }
          >
            <figure>
              <img
                src={require(`../../assets/images/${item.image}`)}
                alt={item.singerName}
              />
            </figure>
            <figcaption>{item.singerName}</figcaption>
            <p>{item.albumsName}</p>
            {this.state.isPlaying && idx === 0 ? (
              <i className="las la-pause"></i>
            ) : (
              <i className="las la-play"></i>
            )}
          </div>
        ))}
      </div>
    );
  }
}

export default TrackList;
