import React from "react";
import Artist from "../artist/artist";
import Album from "../album/album";

import "./content.scss";

class Content extends React.Component {
  handleScroll = (e) => {
    const el = e.target;
    const bottom = el.scrollHeight - el.scrollTop === el.clientHeight;
    if (bottom) {
      this.props.scroll_app();
    }
  };

  render() {
    return (
      <div className="content" onScroll={this.handleScroll}>
        <AlbumsList
          data_artist={this.props.data_content.recently}
          playMusic_artist={this.props.playMusic_content}
          play_artist={this.props.play_content}
        ></AlbumsList>
        <AlbumsList
          data_artist={this.props.data_content.all}
          playMusic_artist={this.props.playMusic_content}
          play_artist={this.props.play_content}
        ></AlbumsList>

        <Album />
      </div>
    );
  }
}

export default Content;
