import React from "react";
import Aside from "./components/aside/aside";
import Content from "./components/content/content";
import Header from "./components/header/header";
import Footer from "./components/footer/footer";

import "./App.scss";

class App extends React.Component {
  constructor(props) {
    super(props);
  }

  state = {
    currentPage: "home",
    searchText: "",
    isLoaded: false,
    track: {
      image: "arash.jpeg",
      trackName: "harchi_1",
      trackUrl: "harchi_1.mp3",
      singerName: "Arash"
    },
    data: {
      recently: {
        title: "Recently played",
        tracks: [
          {
            image: "Katy_Perry_Smile.png",
            singerName: "Kety Perry",
            albumsName: "smile",
          },
          {
            image: "arash.jpeg",
            singerName: "Arash",
            albumsName: "harchi",
          },
          {
            image: "alesso.jpeg",
            singerName: "alesso",
            albumsName: "chert",
          }
        ]
      },
      all: {
        title: "Tracks list",
        tracks: [
          {
            image: "Katy_Perry_Smile.png",
            singerName: "Kety Perry",
            albumsName: "smile",
          },
          {
            image: "arash.jpeg",
            singerName: "Arash",
            albumsName: "harchi",
          },
          {
            image: "alesso.jpeg",
            singerName: "alesso",
            albumsName: "chert",
          },
          {
            image: "rap.jpg",
            singerName: "Adel",
            albumsName: "adel",
          },
          {
            image: "arash.jpeg",
            singerName: "Arash",
            albumsName: "harchi",
          },
          {
            image: "alesso.jpeg",
            singerName: "alesso",
            albumsName: "chert",
          }
        ]
      }
    }
  };

  handleAsideClick = (page) => {
    this.setState({
      currentPage: page,
    });
    console.log("page:", page);
  };

  handleSearch = (singer) => {
    console.clear();
    console.log(singer);
    this.loadAlbums(singer);
  };

  loadAlbums = (singer) => {
    fetch("https://api.spotify.com/v1/search")
      .then(res => res.json())
      .then(
        (result) => {
          this.setState({
            isLoaded: true,
            tracks: result.items
          });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error
          });
        }
      )

    const recivedData = [
      {
        singer: "fari 1",
        track: "track 1",
      },
      {
        singer: "fari 2",
        track: "track 2",
      },
    ];

    this.setState({
      data: recivedData,
    });
  };

  loadMore = () => {
    let newTracks = this.state.tracks;
    let newData = newTracks.all.data;
    newData = [...newData, ...newData];
    newTracks.all.data = newData;

    this.setState({
      tracks: newTracks,
    });

    console.log("loadMore: ", newTracks)
  }

  playMusic = (title, idx) => {
    const tracks = this.state.tracks;
    let newTracks = { ...tracks };
    let newRecentlyData = newTracks.recently.data;

    if (title == 'Recently played') {
      const removedTrack = newRecentlyData.splice(idx, 1);
      newRecentlyData = [removedTrack[0], ...newRecentlyData];
    } else if (title == "Tracks list") {
      const selectedTrackFromAll = this.state.tracks.all.data[idx];
      newRecentlyData = [selectedTrackFromAll, ...newRecentlyData];
    }

    newRecentlyData = newRecentlyData.slice(0, 6);
    newTracks.recently.data = newRecentlyData;

    this.setState({
      tracks: newTracks
    })
  }

  render() {
    return (
      <>
        <Aside action={this.handleAsideClick} />
        <Header
          page={this.state.currentPage}
          title={this.state.currentPage}
          action={this.handleSearch}
        />
        <Content
          recentlyPlayed_app={this.state.tracks.recently}
          tracks_app={this.state.tracks.all}
          scrollAction_app={this.loadMore}
          playAction_app={this.playMusic}
        />
        <Footer data={this.state.track} />
      </>
    );
  }
}

export default App;
