import React from "react";
import Artist from "../artist/artist";

import "./content.scss";

class Content extends React.Component {
  handleScroll = (e) => {
    const div = e.target;
    const bottom = div.scrollHeight - div.scrollTop === div.clientHeight;
    if (bottom) {
      this.props.action();
    }
  };

  render() {
    console.log("CONTENT 1-1");
    return (
      <div className="content" onScroll={this.handleScroll}>
        <Artist data={this.props.data} />
      </div>
    );
  }
}

export default Content;
