import React from "react";
import "./track.scss";

class Track extends React.Component {
  state = {
    playing: 1,
  };

  render() {
    console.log(this.props);
    return (
      <div className="track">
        <span>
          {this.props.isPlaying === true && (
            <>
              <span>
                <i className="las la-music"></i>
              </span>
              <span>
                <i className="las la-pause"></i>
              </span>
            </>
          )}
          {this.props.isPlaying === false && (
            <>
              <span>{this.props.id + 1}</span>
              <span>
                <i className="las la-play"></i>
              </span>
            </>
          )}
        </span>
        <span>
          <figure>
            <img
              src={require(`../../assets/images/${this.props.info.image}`)}
              alt=" {this.props.info.title}"
            />
          </figure>
          {this.props.info.image}
          {this.props.info.title}
        </span>
        <span>{this.props.info.album}</span>
        <span>{this.props.info.date}</span>
        <span>{this.props.info.time}</span>
      </div>
    );
  }
}

export default Track;
