import React from "react";
import GreenBtn from "../greenBtn/greenBtn";

import "./album.scss";

class Album extends React.Component {
  constructor(props) {
    super(props);
  }
  state = {
    track: {
      image: "Katy_Perry_Smile.png",
      singerName: "Kety Perry",
      albumsName: "smile",
    },
    tracks: [
      {
        name: "Montania",
        title: "Calma Interrior",
        album: "Montania",
        date: "11 days ago",
        time: "02:58",
      },
    ],
  };

  render() {
    return (
      <div id="album">
        <header>
          <figure>
            <img
              src={require(`../../assets/images/${this.state.track.image}`)}
              alt={this.state.track.singerName}
            />
          </figure>
          <div id="discription">
            <h1>{this.state.track.albumsName}</h1>
            <p>{this.state.track.singerName}</p>
          </div>
        </header>

        <section>
          <GreenBtn />
          <i className="las la-heart" id="heart"></i>
        </section>

        <section id="trackLists">
          <div>
            <span>#</span>
            <span>TITLE</span>
            <span>ALBUM</span>
            <span>DATE ADDED</span>
            <span>
              <i class="las la-clock"></i>
            </span>
          </div>
        </section>
      </div>
    );
  }
}

export default Album;
