import React from "react";
import "./trackList.scss";

class TrackList extends React.Component {
  handlePlaying = (idx) => {
    this.props.playMusic_artist(this.props.data_artist.title, idx);
  };

  isPlaying = (idx) => {
    const data = this.props.data_artist;
    const cond_1 = this.props.play_artist.active;
    const cond_2 = data.title == "Recently played" && idx == 0;
    const cond_3 =
      data.title == "Tracks list" && idx == this.props.play_artist.idx;
    return (condition_1 && condition_2) || (condition_1 && condition_3);
  };

  render() {
    console.log(
      this.props.data_artist.title,
      this.props.data_artist.title === "Tracks list"
    );
    return (
      <div id="trackList">
        <h2>{this.props.data_artist.title}</h2>
        {this.props.data_artist.tracks.map((item, idx) => (
          <div
            onClick={() => {
              this.handlePlaying(idx);
            }}
            key={idx}
            className={"albums" + (this.isPlaying(idx) ? " active" : "")}
          >
            <figure>
              <img
                src={require(`../../assets/images/${item.image}`)}
                alt={item.singerName}
              />
            </figure>
            <figcaption>{item.singerName}</figcaption>
            <p>{item.albumsName}</p>
            {this.isPlaying(idx) ? (
              <i className="las la-pause"></i>
            ) : (
              <i className="las la-play"></i>
            )}
          </div>
        ))}
      </div>
    );
  }
}

export default TrackList;
