import React from "react";
import axios from 'axios';
import Aside from "./components/aside/aside";
import Content from "./components/content/content";
import Header from "./components/header/header";
import Footer from "./components/footer/footer";

import "./App.scss";

class App extends React.Component {
  constructor(props) {
    super(props);
  }

  state = {
    currentPage: "home",
    searchText: "",
    isLoaded: false,
    offset: 0,
    play: {
      section: '',
      idx: null,
      active: false,
    },
    track: {
      image: "arash.jpeg",
      trackName: "harchi_1",
      trackUrl: "harchi_1.mp3",
      singerName: "Arash"
    },
    data: {
      recently: {
        id: 1,
        title: "Recently played",
        tracks: [
          {
            image: "Katy_Perry_Smile.png",
            singerName: "Kety Perry",
            albumsName: "smile",
          },
          {
            image: "arash.jpeg",
            singerName: "Arash",
            albumsName: "harchi",
          },
          {
            image: "alesso.jpeg",
            singerName: "alesso",
            albumsName: "chert",
          },

        ]
      },
      all: {
        id: 2,
        title: "Tracks list",
        tracks: [
          {
            image: "Katy_Perry_Smile.png",
            singerName: "Kety Perry",
            albumsName: "smile",
          },
          {
            image: "arash.jpeg",
            singerName: "Arash",
            albumsName: "harchi",
          },
          {
            image: "alesso.jpeg",
            singerName: "alesso",
            albumsName: "chert",
          },
          {
            image: "rap.jpg",
            singerName: "Adel",
            albumsName: "adel",
          },
          {
            image: "arash.jpeg",
            singerName: "Arash",
            albumsName: "harchi",
          },
          {
            image: "alesso.jpeg",
            singerName: "alesso",
            albumsName: "chert",
          }
        ]
      }
    }
  };

  handleAsideClick = (page) => {
    this.setState({
      currentPage: page,
    });
    console.log(page)
  };

  handleSearch = (singer) => {
    console.clear();
    console.log(singer);
    this.loadAlbums(singer);
  };

  loadTracks = (albumId) => {
    const access_token = "BQAeNOoxz2legi_hzUJVYC8TCkTkfcHawhPB_mPDD_enJjYhg3yp4nvcZzqm39RfOsnYnk7ktUi6LwGlOBgd6sc7Om9OsGLhZ8ONh4DbeiRuJJkqj3fSB4DexW6O6x2Ix9iX_FMFPbARjApvNvXOlsUJY1btWXa2OjE";
    const headers = {
      'Authorization': `Bearer ${access_token}`,
      'Content-Type': 'application/json',
      'Accept': 'application/json',
    };

    axios.get(
      `https://api.spotify.com/v1/albums/${albumId}/tracks`,
      { headers: headers }
    ).then(res => {
      console.log(res)
      // if (res.data) {
      //   const tracks = res.data.tracks.items;
      //   console.log(tracks)
      // }
    }).catch(err => {
      console.log(err)
    })
  }

  loadAlbums = (singer) => {
    const access_token = "BQAeNOoxz2legi_hzUJVYC8TCkTkfcHawhPB_mPDD_enJjYhg3yp4nvcZzqm39RfOsnYnk7ktUi6LwGlOBgd6sc7Om9OsGLhZ8ONh4DbeiRuJJkqj3fSB4DexW6O6x2Ix9iX_FMFPbARjApvNvXOlsUJY1btWXa2OjE";
    const headers = {
      'Authorization': `Bearer ${access_token}`,
      'Content-Type': 'application/json',
      'Accept': 'application/json',
    };
    singer = "CageThe%20Elephant";
    const params = `q=${singer}&type=track&offset=${this.state.offset}&limit=6`;

    axios.get(
      `https://api.spotify.com/v1/search?${params}`,
      { headers: headers }
    ).then(res => {
      if (res.data) {
        const tracks = res.data.tracks.items;
        console.log(tracks);
        const albumId = tracks[0].id;
        this.loadTracks(albumId);
      }
    }).catch(err => {
      console.log(err)
    })


    // const recivedData = [
    //   {
    //     singer: "fari 1",
    //     track: "track 1",
    //   },
    //   {
    //     singer: "fari 2",
    //     track: "track 2",
    //   },
    // ];

    // this.setState({
    //   data: recivedData,
    // });
  };

  loadMore = () => {
    this.loadAlbums();
    return

    let newTracks = this.state.tracks;
    let newData = newTracks.all.data;
    newData = [...newData, ...newData];
    newTracks.all.data = newData;

    this.setState({
      tracks: newTracks,
    });

    console.log("loadMore: ", newTracks)
  }

  playMusic = (title, idx) => {
    let newData = { ...this.state.data };
    let newRecentlyTracks = [...newData.recently.tracks];

    if (title == 'Recently played') {
      const removedTrack = newRecentlyTracks.splice(idx, 1);
      newRecentlyTracks = [removedTrack[0], ...newRecentlyTracks];
    } else if (title == "Tracks list") {
      const selectedTrackFromAll = this.state.data.all.tracks[idx];
      newRecentlyTracks = [selectedTrackFromAll, ...newRecentlyTracks];
    }

    let newPlay = { ...this.state.play };

    let isPlaying = true;
    if (idx === 0 && title === "Recently played") {
      isPlaying = !this.state.data.recently.isPlaying;
    }

    newPlay.section = title;
    newPlay.idx = idx;
    newPlay.active = isPlaying;

    newRecentlyTracks = newRecentlyTracks.slice(0, 6);
    newData.recently.tracks = newRecentlyTracks;
    newData.recently.isPlaying = isPlaying;

    this.setState({
      play: newPlay,
    }, () => {
      console.log(this.state.play)
    });


    this.setState({
      data: newData,
    }, () => {
      console.log(this.state.play)
    });

  }

  render() {
    return (
      <>
        <Aside action_={this.handleAsideClick} />
        <Header
          page={this.state.currentPage}
          title={this.state.currentPage}
          action={this.handleSearch}
        />
        <Content
          data_app={this.state.data}
          scroll_app={this.loadMore}
          playMusic_app={this.playMusic}
          play_app={this.state.play}
        />
        <Footer data={this.state.track} />
      </>
    );
  }
}

export default App;
