import React from "react";
import "./artist.scss";

class Artist extends React.Component {
  constructor(props) {
    super(props);
  }
  state = {
    isPlaying: false,
  };
  playHandler = () => {
    this.setState({
      isPlaying: true,
    });
  };
  render() {
    return (
      <div id="artist">
        {this.props.data.map((item, idx) => (
          <div
            key={idx}
            className={
              this.state.isPlaying === false ? "albums" : "albums active"
            }
          >
            <figure>
              <a href="">
                <img
                  src={require(`../../assets/images/${item.image}`)}
                  alt={item.singerName}
                />
              </a>
            </figure>
            <figcaption>{item.singerName}</figcaption>
            <p>{item.albumsName}</p>
            {this.state.isPlaying ? (
              <i className="las la-pause"></i>
            ) : (
              <i className="las la-play" onClick={this.playHandler}></i>
            )}
            ;
          </div>
        ))}
      </div>
    );
  }
}

export default Artist;
