import React from "react";
import "./artist.scss";

class Artist extends React.Component {
  constructor(props) {
    super(props);
  }
  state = {
    isPlaying: true,
  };

  render() {
    return (
      <div id="artist">
        {this.props.data.map((item, idx) => (
          <div key={idx} className="albums">
            <figure>
              <a href="">
                <img
                  src={require(`../../assets/images/${item.image}`)}
                  alt={item.singerName}
                />
              </a>
            </figure>
            <figcaption>{item.singerName}</figcaption>
            <p>{item.albumsName}</p>
            {this.state.isPlaying ? (
              <i class="las la-pause"></i>
            ) : (
              <i class="las la-play"></i>
            )}
            ;
          </div>
        ))}
      </div>
    );
  }
}

export default Artist;
