import React from "react";
import "./footer.scss";

class Footer extends React.Component {
  constructor(props) {
    super(props);
  }
  state = {
    artist: [],
  };
  render() {
    return (
      <div className="footer">
        <div id="track">
          <figure>
            <img
              src={require(`../../assets/images/${this.props.data.image}`)}
              alt="this.props.data.trackName"
            />
          </figure>
          <figcaption>
            {this.props.data.trackName}
            <p>{this.props.data.singerName}</p>
          </figcaption>
          <i class="las la-heart"></i>
        </div>

        <div id="playBox">
          <div>
            <i class="las la-random"></i>
            <i class="las la-fast-backward"></i>
            <i class="las la-play"></i>
            <i class="las la-pause"></i>
            <i class="las la-forward"></i>
            <i class="las la-redo-alt"></i>
          </div>
          <div id="progressBar">
            <span>2.14</span>
            <span id="progressLine"></span>
            <span>4.54</span>
          </div>
        </div>

        <div id="audioBox"></div>
      </div>
    );
  }
}

export default Footer;
