import React from "react";
import "./albumsList.scss";

class AlbumsList extends React.Component {
  handlePlaying = (idx) => {
    this.props.playMusic_artist(this.props.data_artist.title, idx);
  };

  isPlaying = (idx) => {
    const data = this.props.data_artist;
    const playInfo = this.props.play_artist;

    const cond_1 = playInfo.active;
    const cond_2 = data.id == 1 && idx == 0;
    const cond_3 = data.id == 2 && idx == playInfo.idx;

    console.log(
      this.props.data_artist.title,
      ":",
      idx,
      ":",
      cond_1 && cond_2,
      cond_1 && cond_3
    );
    return (cond_1 && cond_2) || (cond_1 && cond_3);
  };

  render() {
    return (
      <div id="trackList">
        <h2>{this.props.data_content.title}</h2>
        {this.props.data_content.tracks.map((item, idx) => (
          <div
            onClick={() => {
              this.handlePlaying(idx);
            }}
            key={idx}
            className={"albums" + (this.isPlaying(idx) ? " active" : "")}
          >
            <figure>
              <img
                src={require(`../../assets/images/${item.image}`)}
                alt={item.singerName}
              />
            </figure>
            <figcaption>{item.singerName}</figcaption>
            <p>{item.albumsName}</p>
            {this.isPlaying(idx) ? (
              <i className="las la-pause"></i>
            ) : (
              <i className="las la-play"></i>
            )}
          </div>
        ))}
      </div>
    );
  }
}

export default AlbumsList;
