import React from "react";
import "./artist.scss";

class Artist extends React.Component {
  constructor(props) {
    super(props);
  }

  state = {
    isPlaying: false,
  };

  playHandler = (idx) => {
    let newStatus = !this.state.isPlaying;

    if (idx !== 0) {
      newStatus = true;
      const artists = [...this.props.artists];
      artists.splice(idx, 1);
      const newArtists = [this.props.artists[idx], ...artists];

      this.setState({
        artists: newArtists,
      });
    }

    this.setState({
      isPlaying: newStatus,
    });
  };

  render() {
    return (
      <div id="artist">
        {this.props.artists.map((item, idx) => (
          <div
            onClick={() => {
              this.playHandler(idx);
            }}
            key={idx}
            className={
              "albums" + (this.state.isPlaying && idx === 0 ? " active" : "")
            }
          >
            <figure>
              <img
                src={require(`../../assets/images/${item.image}`)}
                alt={item.singerName}
              />
            </figure>
            <figcaption>{item.singerName}</figcaption>
            <p>{item.albumsName}</p>
            {this.state.isPlaying && idx === 0 ? (
              <i className="las la-pause"></i>
            ) : (
              <i className="las la-play"></i>
            )}
          </div>
        ))}
      </div>
    );
  }
}

export default Artist;
