import React from "react";
import "./artist.scss";

class Artist extends React.Component {
  constructor(props) {
    super(props);
  }
  state = {};
  render() {
    console.log(typeof this.props.data[0].image);
    return (
      <div id="artist">
        {this.props.data.map((item, idx) => (
          <div key={idx} className="albums">
            <figure>
              <a href="">
                <img
                  src={require(`../../assets/images/${
                    Katy_Perry_ - _Smile.png
                  }`)}
                  alt={item.singerName}
                />
              </a>
            </figure>
            <figcaption>{item.singerName}</figcaption>
            <div>{item.albumsName}</div>
          </div>
        ))}
      </div>
    );
  }
}

export default Artist;
