import React, { useRef } from "react";
import "./footer.scss";

class Footer extends React.Component {
  constructor(props) {
    super(props);
  }
  state = {
    artist: [],
    volume: {
      value: 0,
      isMoving: false,
    },
    isPlaying: false,
  };

  playHandler = () => {
    let newState = !this.state.isPlaying;
    this.setState({
      isPlaying: newState,
    });
  };
  mouseDownHandler = () => {
    let newVolume = this.state.volume;
    newVolume.isMoving = true;

    this.setState({
      volume: newVolume,
    });
  };

  mouseUpHandler = () => {
    let newVolume = this.state.volume;
    newVolume.isMoving = false;

    this.setState({
      volume: newVolume,
    });
  };

  clickHandler = (e) => {
    const box = e.target.getBoundingClientRect();
    const mouseLeft = parseInt(e.clientX);
    const boxLeft = box.left;
    const diff = mouseLeft - boxLeft;
    const boxWidth = parseInt(box.width);

    if (diff < 0) diff = 0;
    const percent = (100 * diff) / boxLeft;

    console.log(
      `mouseLeft: ${mouseLeft}, boxLeft: ${boxLeft}, diff: ${diff}, boxWidth: ${boxWidth}, percent: ${percent}%`
    );
  };

  render() {
    return (
      <div className="footer">
        <div id="info">
          <figure>
            <img
              src={require(`../../assets/images/${this.props.data.image}`)}
              alt="this.props.data.trackName"
            />
          </figure>
          <figcaption>
            {this.props.data.trackName}
            <p>{this.props.data.singerName}</p>
          </figcaption>
          <div className="icons">
            <i className="las la-heart"></i>
            <i className="las la-share-square"></i>
          </div>
        </div>

        <div id="controller">
          <div>
            <i className="las la-random"></i>
            <i className="las la-fast-backward"></i>
            {this.state.isPlaying === false ? (
              <i className="las la-play" onClick={this.playHandler}></i>
            ) : (
              <i className="las la-pause" onClick={this.playHandler}></i>
            )}

            <i className="las la-forward"></i>
            <i className="las la-redo-alt"></i>
          </div>
          <div id="progressBar">
            <span>2.14</span>
            <span id="firstProgressLine"></span>
            <span id="secondProgressLine"></span>
            <span>4.54</span>
          </div>
        </div>

        <div id="audio" className="icons">
          <i className="las la-microphone-alt"></i>
          <i className="las la-bars"></i>
          <i className="lab la-connectdevelop"></i>

          {this.state.volume.value == 0 && (
            <i className="las la-volume-mute"></i>
          )}

          {this.state.volume.value !== 0 && this.state.volume.value !== 100 && (
            <i className="las la-volume-down"></i>
          )}

          {this.state.volume.value == 100 && (
            <i className="las la-volume-up"></i>
          )}

          <span
            id="firstAudioMeasure"
            onMouseDown={this.mouseDownHandler}
            onMouseUp={this.mouseUpHandler}
            onClick={this.clickHandler}
          >
            <span id="secondAudioMeasure">
              <span id="circle"></span>
            </span>
          </span>
        </div>
      </div>
    );
  }
}

export default Footer;
