import React from "react";
import GreenBtn from "../greenBtn/greenBtn";

import "./album.scss";

class Album extends React.Component {
  constructor(props) {
    super(props);
  }
  state = {
    tracks: {
      image: "Katy_Perry_Smile.png",
      singerName: "Kety Perry",
      albumsName: "smile",
    },
  };

  render() {
    return (
      <div id="album">
        <header>
          <figure>
            <img
              src={require(`../../assets/images/${this.state.tracks.image}`)}
              alt={this.state.tracks.singerName}
            />
          </figure>
          <div id="discription">
            <h1>{this.state.tracks.albumsName}</h1>
            <p>{this.state.tracks.singerName}</p>
          </div>
        </header>

        <section>
          <GreenBtn />
          <i className="las la-heart" id="heart"></i>
        </section>
      </div>
    );
  }
}

export default Album;
