import React from "react";
import "./footer.scss";

class Footer extends React.Component {
  constructor(props) {
    super(props);
  }
  state = {
    artist: [],
  };
  render() {
    return (
      <div className="footer">
        {this.state.artist.map((item, idx) => {
          <div key={idx} className="track">
            <figure>
              <img src={require(`../../assets/images/${item.image}`)} alt="" />
            </figure>
          </div>;
        })}
        <div id="playBox">
          <i class="las la-random"></i>
          <i class="las la-fast-backward"></i>
          <i class="las la-play"></i>
          <i class="las la-forward"></i>
          <i class="las la-redo-alt"></i>
        </div>
        <div id="progressBar">
          <span>2.14</span>
          <span id="progressLine"></span>
          <span>4.54</span>
        </div>
      </div>
    );
  }
}

export default Footer;
