import React from "react";
import "./trackList.scss";

class TrackList extends React.Component {
  state = {
    isPlaying: false,
  };

  handlePlaying = (idx) => {
    let isPlaying = !this.state.isPlaying;
    if (idx !== 0) {
      isPlaying = true;
    }
    this.setState(
      {
        isPlaying,
      },
      () => {
        this.props.action(this.props.title, idx);
      }
    );
  };

  isActive = (idx) => {
    return (
      this.props.title == "Recently played" && this.state.isPlaying && idx === 0
    );
  };

  render() {
    return (
      <div id="trackList">
        <h2>{this.props.title}</h2>
        {this.props.tracks.map((item, idx) => (
          <div
            onClick={() => {
              this.handlePlaying(idx);
            }}
            key={idx}
            className={"albums" + (this.isActive(idx) ? " active" : "")}
          >
            <figure>
              <img
                src={require(`../../assets/images/${item.image}`)}
                alt={item.singerName}
              />
            </figure>
            <figcaption>{item.singerName}</figcaption>
            <p>{item.albumsName}</p>
            {this.isActive(idx) ? (
              <i className="las la-pause"></i>
            ) : (
              <i className="las la-play"></i>
            )}
          </div>
        ))}
      </div>
    );
  }
}

export default TrackList;
