import React from "react";
import GreenBtn from "../greenBtn/greenBtn";
import Track from "../track/track";

import "./album.scss";

class Album extends React.Component {
  constructor(props) {
    super(props);
  }
  state = {
    track: {
      image: "Katy_Perry_Smile.png",
      singer: "Montania",
      title: "Calma Interrior",
      album: "Montania",
      date: "11 days ago",
      time: "02:58",
    },
    tracks: [
      {
        image: "Katy_Perry_Smile.png",
        singer: "Montania",
        title: "Calma Interrior",
        album: "Montania",
        date: "11 days ago",
        time: "02:58",
      },
      {
        image: "Katy_Perry_Smile.png",
        singer: "Montania",
        title: "Calma Interrior",
        album: "Montania",
        date: "11 days ago",
        time: "02:58",
      },
    ],
  };

  render() {
    return (
      <div id="album">
        <header>
          <figure>
            <img
              src={require(`../../assets/images/${this.state.track.image}`)}
              alt={this.state.track.singer}
            />
          </figure>
          <div id="discription">
            <h1>{this.state.track.album}</h1>
            <p>{this.state.track.singer}</p>
          </div>
        </header>

        <section>
          <GreenBtn />
          <i className="las la-heart" id="heart"></i>
        </section>

        <section id="tracksList">
          <div>
            <span>#</span>
            <span>TITLE</span>
            <span>ALBUM</span>
            <span>DATE ADDED</span>
            <span>
              <i class="las la-clock"></i>
            </span>
          </div>
          {this.state.tracks.map((item, idx) => (
            <Track info={item} id={idx} key={idx} />
          ))}
        </section>
      </div>
    );
  }
}

export default Album;
